import React, { useState, useRef, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';

interface Message {
    text: string;
    isUser: boolean;
}

const N8N_WEBHOOK_URL = "YOUR_N8N_WEBHOOK_URL_HERE";

export const AiTutor: React.FC = () => {
    const [messages, setMessages] = useState<Message[]>([{
        text: "ðŸ‘‹ Hello! I'm your AI tutor. How can I help you study today?",
        isUser: false
    }]);
    const [inputValue, setInputValue] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [isVisible, setIsVisible] = useState(true);

    const chatOutputRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        if (chatOutputRef.current) {
            chatOutputRef.current.scrollTop = chatOutputRef.current.scrollHeight;
        }
    }, [messages]);

    const appendMessage = (text: string, isUser: boolean) => {
        setMessages(prev => [...prev, { text, isUser }]);
    };

    const handleSendMessage = async () => {
        const prompt = inputValue.trim();
        if (!prompt || isLoading) return;

        setInputValue('');
        setIsLoading(true);
        appendMessage(prompt, true);

        try {
            const response = await fetch(N8N_WEBHOOK_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ prompt }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            appendMessage(data.response || "I apologize, but I didn't receive a valid response. Please try again.", false);

        } catch (error) {
            console.error('Error:', error);
            appendMessage("I'm sorry, but I encountered an error. Please check your connection and try again.", false);
        } finally {
            setIsLoading(false);
        }
    };

    const handleKeyPress = (e: React.KeyboardEvent) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <div 
            className={`fixed bottom-4 right-4 z-50 bg-white rounded-lg shadow-lg overflow-hidden flex flex-col w-full max-w-sm transform transition-all duration-300 ease-in-out ${
                isVisible 
                    ? 'translate-y-0 opacity-100 h-[500px]' 
                    : 'translate-y-full opacity-0 h-0'
            }`}
        >
            <div className="bg-blue-600 text-white p-3 font-bold rounded-t-lg flex justify-between items-center">
                <span>AI Tutor</span>
                <button 
                    onClick={() => setIsVisible(!isVisible)}
                    className="text-white hover:text-gray-200 focus:outline-none transition-transform duration-200"
                >
                    <svg 
                        xmlns="http://www.w3.org/2000/svg" 
                        className={`h-6 w-6 transform ${isVisible ? 'rotate-0' : 'rotate-180'}`}
                        fill="none" 
                        viewBox="0 0 24 24" 
                        stroke="currentColor"
                    >
                        <path 
                            strokeLinecap="round" 
                            strokeLinejoin="round" 
                            strokeWidth={2} 
                            d="M19 9l-7 7-7-7" 
                        />
                    </svg>
                </button>
            </div>

            <div className="flex flex-col flex-1">
                <div 
                    ref={chatOutputRef}
                    className="flex-1 p-4 overflow-y-auto bg-gray-50 text-sm"
                >
                    {messages.map((message, index) => (
                        <div 
                            key={index}
                            className={`my-2 ${message.isUser ? 'ml-auto' : 'mr-auto'} max-w-[80%]`}
                        >
                            <div 
                                className={`p-3 rounded-lg ${
                                    message.isUser 
                                        ? 'bg-blue-500 text-white' 
                                        : 'bg-gray-200 text-gray-800'
                                }`}
                            >
                                <ReactMarkdown>{message.text}</ReactMarkdown>
                            </div>
                        </div>
                    ))}
                </div>

                <div className="p-3 border-t border-gray-200 flex">
                    <input
                        type="text"
                        value={inputValue}
                        onChange={(e) => setInputValue(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder="Ask your question..."
                        className="flex-1 p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                        disabled={isLoading}
                    />
                    <button
                        onClick={handleSendMessage}
                        disabled={isLoading}
                        className={`ml-2 px-4 py-2 bg-blue-600 text-white rounded-lg transition duration-300 ease-in-out ${
                            isLoading 
                                ? 'opacity-50 cursor-not-allowed' 
                                : 'hover:bg-blue-700'
                        }`}
                    >
                        {isLoading ? '...' : 'Send'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default AiTutor;
